import Letter from './Letter'
export default function Letters({rowNum, alphabet}) {
    let numInRow = Math.ceil(alphabet.length / rowNum)
    let dividedAlphabet = Array(rowNum).fill([]).map((row, index) => {
        return Array(numInRow).fill(0).map((item, ind) => {
            item = alphabet[index*numInRow + ind]
            return item
        })
    })

    // for (let i = 0 ; i < rowNum; i++) {

    //     dividedAlphabet.push(part)
    // }
    console.log(dividedAlphabet)
    return (
        <div className="letters">
            {dividedAlphabet.map(el => {
                return (
                    <div className="letters__row">
                        {el.map(letter => <Letter key={letter.name} letter={letter}/>)}
                    </div>
                )
            })}
        </div>
    )
}
